INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (2, 'harlem', 'A People''s History of Harlem', 'A Harlem Neighborhood Oral History Project', '', '<p>This is a neighborhood oral history project that works to both preserve 
and document Harlem history through the stories of people who have 
experienced it. This project will collect oral histories of people who 
have lived or worked in the surrounding Harlem neighborhood and train 
community members to conduct these interviews. Both longtime and more recent 
residents are invited to share their neighborhood stories, documenting 
Harlem’s past and present history. Interviews will be preserved at The <a>Milstein Division</a>, available in a circulating collection, and accessible here at the <a>New York Public Library website</a>.</p><br>', '<p><small>
For more information about this project, please contact Alexandra Kelly at Outreach Services and Adult Programs, 
AlexandraKelly@nypl.org or (212) 621-0552. 
</small></p>', 0, 'harlem.jpg', '2014-05-02 19:37:45.565958', '2014-07-15 14:30:17.305559', 1);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (1, 'greenwich-village', 'Your Village, Your Story', 'Greenwich Village Oral History Project', '', '<p>
This is a community-based oral history project at Jefferson Market Library that works to both preserve and document Greenwich Village neighborhood history through 
the stories of people who have experienced it.  
</p>
<p>
From December 2013 - April 2014, trained volunteer interviewers worked to collect over 100 oral histories of people who have a longtime 
relationship with Greenwich Village.  This collection represents the voices of individuals who have lived, worked or spent over 20 years in the neighborhood.
</p>
<p>
We invite you to browse these oral histories - unedited interviews between Interviewers and Storytellers.  These recordings will also be accessible at The New York 
Public Library in The Milstein Division, in circulating collection at Jefferson Market Library, and on this website.
</p>', '<p><small>
For more information about this project, please contact Alexandra Kelly at Outreach Services and Adult Programs, 
AlexandraKelly@nypl.org or (212) 621-0552. 
</small></p>', 0, 'greenwich_village.jpg', '2014-05-02 19:28:41.286384', '2014-07-15 14:30:38.114327', 1);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (4, 'east-bronx', 'Voices from East of Bronx Park', 'An Oral History Project of the Allerton, Van Nest, Pelham Parkway and Morris Park Communities', '', '<p>


















</p><p><b><i>Voices
from East of Bronx Park</i></b><b> </b>is an oral history project
that works to both preserve and document the neighborhood history of the Allerton,
Pelham Parkway, Morris Park, and Van Nest communities through the stories of
people who have experienced it. The project will collect oral histories of
people who live in neighborhoods east of Bronx Park and train community members
to conduct these interviews. Longtime residents and those who have worked in
these neighborhoods are invited to share their stories, documenting an
important past and present history. Interviews will be preserved at The <a>Milstein Division</a> of United States History, Local
History, and Genealogy. Interviews will also be available in a circulating
collection and accessible on this website.</p>', '<p><small>
For more information about this project, please contact Alexandra Kelly at Outreach Services and Adult Programs, 
AlexandraKelly@nypl.org or (212) 621-0552. 
</small></p>', 0, 'east_bronx.jpg', '2014-10-14 17:50:09.56306', '2014-10-23 16:34:25.184983', 1);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (6, 'stapleton', 'Stapleton Speaks', 'Our Neighborhood Oral History Project', '', '<p>This is a neighborhood oral history project that works to both preserve and document Stapleton history through the stories of people who have experienced it. This project will collect oral histories of people who have lived or worked in the Stapleton neighborhood. Volunteer interviewers have been&nbsp;trained to collect these stories and all interviews will be preserved at The Milstein Division of US History, Local History and Genealogy and available in a circulating collection. Interviews will also be accessible in full length at&nbsp;<a>this website</a>.<br></p><br>', '<p><small>For more information about this project, please contact Alexandra Kelly at Outreach Services and Adult Programs, AlexandraKelly@nypl.org or (212) 621-0552.</small></p>', 0, 'stapleton.jpg', '2015-04-29 19:59:59.784811', '2015-06-26 13:55:35.05336', 1);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (3, 'visible-lives', 'Visible Lives', 'Oral Histories of the Disability Experience', '
', '<p><b><i>Visible Lives: Oral Histories of the Disability Experience</i></b><b>&nbsp;</b>is an oral history project that works to both preserve and document a thematic history through&nbsp;personal recollections.&nbsp;This project will collect stories of people who are living with a disability and the&nbsp;Library will train community members to conduct these interviews. Interviews will be shared in a preservation archive at The Milstein Division and on the New York Public Library website.&nbsp;&nbsp;Public programs will also connect neighborhood residents and project participants.</p><p><b><i>Visible Lives</i></b><i>&nbsp;</i>is a project of Andrew Heiskell Braille and Talking Book Library in Manhattan.&nbsp; A public archive will be kept at this local branch for future generations to listen to and research.&nbsp;</p>', '<p><b>For
more information about this project or to share YOUR story:</b> &nbsp;Please contact Alexandra Kelly at Outreach
Services and Adult Programming, AlexandraKelly@nypl.org or (212) 621-0552.</p>', 0, 'visible_lives.jpg', '2014-10-14 17:46:51.988278', '2015-02-04 20:00:12.310032', 1);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (7, 'old-bronx-courthouse', 'No Longer Empty at Old Bronx Borough Courthouse', '', '', '<p>Join <a>No Longer Empty</a> and the New York Public Library Community Oral History Program as we invite the Melrose and Morrisania communities back into the Old Bronx Borough Courthouse (it''s been closed to the public for 37 years!)&nbsp;to collect diverse accounts about the historic landmark building, and surrounding neighborhoods.</p><p>Volunteers will be&nbsp;interviewing their neighbors, family, and colleagues who lived and worked in and around the Old Bronx Borough Courthouse.&nbsp;<br></p><p>Community Oral History interviews will continue on an ongoing basis at 878 Brook Avenue through&nbsp;July 19, 2015&nbsp;and will be collected and archived at the New York Public Library for use by future generations.</p><p>Interviewer Training Sessions will be&nbsp;on the following dates:</p><p>Saturday, May 16 10am -12pm.</p><p>Thursday,&nbsp;May 19 6:30pm - 8pm.</p><p><span>To learn how to interview people about their experience living or working near the Old Bronx Borough Courthouse, please&nbsp;RSVP to Marcus Galen Mitchell at Marcus@nolongerempty.org or 917-318-6691. Please visit No Longer Empty’s event page at&nbsp;<a>nolongerempty.org</a>.&nbsp;</span></p><p>Community Oral History interviews will continue on an ongoing basis at 878 Brook Avenue through&nbsp;July 19, 2015&nbsp;and will be collected and archived at the New York Public Library for use by future generations as well as available at oralhistory.nypl.org. We have several recording dates set up for June and July!</p>', '<p>For more information about this project, please contact Alexandra Kelly at Outreach Services and Adult Programs, 
AlexandraKelly@nypl.org or (212) 621-0552.<br></p>', 0, 'old-bronx-courthouse.jpg', '2015-05-13 17:36:25.611765', '2015-05-15 19:01:04.06608', 0);
INSERT INTO neighborhoods (id, slug, title, subtitle, short_description, long_description, contact_description, interview_count, image, created_at, updated_at, is_featured) VALUES (5, 'washington-heights-inwood', 'Bridging Our Stories', 'Washington Heights and Inwood Oral History Project', 'This is a neighborhood oral history project that works to both preserve and document Washington Heights and Inwood history through the stories of people who have experienced it. This project will collect oral histories of people who have lived or worked in the Washington Heights and Inwood neighborhoods. Community members will be trained to conduct these interviews. Interviews will be preserved at The Milstein Division of US History, Local History and Genealogy and available in a circulating collection. Interviews will also be accessible at the New York Public Library website: oralhistory.nypl.org', '<p>This is a
neighborhood oral history project that works to both preserve and document
Washington Heights and Inwood history through the stories of people who have
experienced it. This project will collect oral histories of people who have
lived or worked in the Washington Heights and Inwood neighborhoods. Over 90 volunteer interviewers have been&nbsp;trained to collect these stories.&nbsp;Interviews will be
preserved at The Milstein Division of US History, Local History and Genealogy
